package mock

import (
	"fmt"
	"log"
	"sync"

	"github.com/GoSimplicity/AI-CloudOps/internal/model"
	"gorm.io/gorm"
)

type K8sClientMock struct {
	sync.RWMutex
	db *gorm.DB
}

func NewK8sClientMock(db *gorm.DB) *K8sClientMock {
	mock := &K8sClientMock{
		db: db,
	}

	mock.populateMockData()

	return mock
}

// populateMockData 插入多个模拟集群和相关数据到数据库
func (m *K8sClientMock) populateMockData() {
	m.Lock()
	defer m.Unlock()

	// 示例：插入多个 K8sCluster
	clusters := []model.K8sCluster{
		{
			Name:          "Cluster-1",
			NameZh:        "集群-1",
			UserID:        1,
			CpuRequest:    "100m",
			CpuLimit:      "200m",
			MemoryRequest: "256Mi",
			MemoryLimit:   "512Mi",
			Env:           "prod",
			Version:       "v1.32.0",
			ApiServerAddr: "https://api.cluster1.example.com",
			KubeConfigContent: `
apiVersion: v1
clusters:
- cluster:
    certificate-authority-data: 
    server: https://192.168.0.104:6443
  name: kubernetes
- cluster:
    certificate-authority-data: 
    server: https://127.0.0.1:59623
  name: kind-kind
contexts:
- context:
    cluster: kubernetes
    user: kubernetes-admin
  name: kubernetes-admin@kubernetes
- context:
    cluster: kind-kind
    user: kind-kind
  name: kind-kind
current-context: kind-kind
kind: Config
preferences: {}
users:
- name: kubernetes-admin
  user:
    client-certificate-data: 
    client-key-data: 
- name: kind-kind
  user:
    client-certificate-data: 
    client-key-data: 
`,
			ActionTimeoutSeconds: 30,
		},
	}

	for _, cluster := range clusters {
		// 插入或更新 K8sCluster
		if err := m.db.Where("name = ?", cluster.Name).FirstOrCreate(&cluster).Error; err != nil {
			log.Printf("populateMockData: 插入 K8sCluster 失败: %v\n", err)
			continue
		}

		log.Printf("populateMockData: 初始化 Kubernetes 集群成功，ClusterID: %d\n", cluster.ID)

		// 为每个集群插入模拟节点
		var nodes []*model.K8sNode

		for i := 0; i < 3; i++ {
			node := &model.K8sNode{
				Name:      fmt.Sprintf("%s-mock-node-%d", cluster.Name, i),
				ClusterID: cluster.ID,
			}
			nodes = append(nodes, node)
		}

		if err := m.db.Create(&nodes).Error; err != nil {
			log.Printf("populateMockData: 插入 Node 失败: %v\n", err)
			continue
		}

		log.Printf("populateMockData: 为集群 %d 插入节点成功\n", cluster.ID)
	}
}
